import { useState, useEffect, useContext } from "react";
import { useNavigate, Route, Routes } from "react-router-dom";
import Header from './components/Header';
import Tasks from './components/Tasks';
import AddTask from "./components/AddTask";
import Footer from "./components/Footer";
import Login from "./components/Login";
import Signup from "./components/Signup";
import { signup, login, fetchTasks, addTask, deleteTask, updateTask } from './api';
import AuthContext from './context/AuthContext';

function App() {
  const [showAddTask, setShowAddTask] = useState(false);
  const [tasks, setTasks] = useState([]);
  const { token, login: authLogin, logout } = useContext(AuthContext);
  const navigate = useNavigate();

  useEffect(() => {

    if (token) {
      fetchTasks(token)
        .then(data => setTasks(data))
        .catch(err => console.error("Error fetching tasks:", err));
    }
  }, [token]);

  const handleLogin = async (userData) => {
    try {
      const response = await login(userData);
      const newToken = response.token;
      authLogin(newToken);
      if (newToken) {
        navigate("/tasks");
      }
    } catch (error) {
      console.error("Server responded with error:", error.response?.data || error.message);
    }
  };

  const handleSignup = async (userData) => {
    try {
      const { token: newToken } = await signup(userData);
      authLogin(newToken);
      navigate("/tasks");
    } catch (err) {
      console.error("Error signing up:", err);
    }
  };

  // Add task
  const handleAddTask = async (task) => {
    try {
      const newTask = await addTask(task, token);
      setTasks([...tasks, newTask]);
    } catch (err) {
      console.error("Error adding task:", err);
    }
  };

  // Delete task
  const handleDeleteTask = async (id) => {
    try {
      await deleteTask(id, token);
      setTasks(tasks.filter((task) => task.id !== id));
    } catch (err) {
      console.error(err);
    }
  };

  // Toggle reminder
  const handleToggleReminder = async (id) => {
    const taskToUpdate = tasks.find(task => task.id === id);
    const updatedTask = { ...taskToUpdate, reminder: !taskToUpdate.reminder };
    try {
      await updateTask(updatedTask, token);
      setTasks(tasks.map(task => task.id === id ? updatedTask : task));
    } catch (err) {
      console.error("Error updating task:", err);
    }
  };

  const updateTask = async(task)=>{
    const taskToUpdate = tasks.find(task => task.id === id);
    const updatedTask = { ...taskToUpdate, reminder: !taskToUpdate.reminder };
    try {
      await updateTask(updatedTask, token);
      setTasks(tasks.map(task => task.id === id ? updatedTask : task));
    } catch (err) {
      console.error("Error updating task:", err);
    }
  }

  return (
    <div className="container">
      <Header 
        title='Welcome Name'
        onAdd={() => setShowAddTask(!showAddTask)}
        showAdd={showAddTask} 
      />
      <Routes>
        <Route 
          path='/' 
          element={<Login onLogin={handleLogin} />}
        />
        <Route 
          path='/signup' 
          element={<Signup onSignup={handleSignup} />}
        />
        <Route 
          path='/tasks'
          element={
            <>
              { showAddTask && <AddTask onAdd={handleAddTask} /> }
              {tasks.length > 0 ? 
                <Tasks tasks={tasks} 
                  onDelete={handleDeleteTask} 
                  onToggle={handleToggleReminder} />
              : 'No tasks to show'}
              <Footer />
            </>
          }
        />
      </Routes>
    </div>
  );
}

export default App;
