import { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Routes, useNavigate } from 'react-router-dom';
import Header from './components/Header';
import Tasks from './components/Tasks';
import AddTask from './components/AddTask';
import Footer from './components/Footer';
import Login from './components/Login';
import Signup from './components/Signup';
import { signup, login, fetchTasks, addTask, deleteTask, updateTask } from './api';

function App() {
  const [showAddTask, setShowAddTask] = useState(false);
  const [tasks, setTasks] = useState([]);
  const [token, setToken] = useState('');

  const navigate = useNavigate();

  useEffect(() => {
    const fetchUserTasks = async () => {
      if (token) {
        const fetchedTasks = await fetchTasks(token);
        setTasks(fetchedTasks);
      }
    };

    fetchUserTasks();
  }, [token]);

  const handleLogin = async (userData) => {
    try {
      const { token } = await login(userData);
      setToken(token);
      navigate('/tasks');
    } catch (error) {
      console.error(error);
    }
  };

  const handleSignup = async (userData) => {
    try {
      const { token } = await signup(userData);
      setToken(token);
      navigate('/tasks');
    } catch (error) {
      console.error(error);
    }
  };

  const handleAddTask = async (task) => {
    const newTask = await addTask(task, token);
    setTasks([...tasks, newTask]);
  };

  const handleDeleteTask = async (taskId) => {
    await deleteTask(taskId, token);
    setTasks(tasks.filter((task) => task.id !== taskId));
  };

  const handleUpdateTask = async (updatedTask) => {
    const updated = await updateTask(updatedTask, token);
    setTasks(tasks.map((task) => (task.id === updated.id ? updated : task)));
  };

  const handleToggleReminder = (id) => {
    const taskToToggle = tasks.find((task) => task.id === id);
    const updatedTask = { ...taskToToggle, reminder: !taskToToggle.reminder };
    handleUpdateTask(updatedTask);
  };

  return (
    <Router>
      <div className="container">
        <Header title="Task Tracker" onAdd={() => setShowAddTask(!showAddTask)} showAdd={showAddTask} />
        <Routes>
          <Route
            path="/tasks"
            element={
              <>
                {showAddTask && <AddTask onAdd={handleAddTask} />}
                {tasks.length > 0 ? (
                  <Tasks tasks={tasks} onDelete={handleDeleteTask} onToggle={handleToggleReminder} />
                ) : (
                  'No tasks to show'
                )}
                <Footer />
              </>
            }
          />
          <Route path="/" element={<Login onLogin={handleLogin} />} />
          <Route path="/signup" element={<Signup onSignup={handleSignup} />} />
        </Routes>
      </div>
    </Router>
  );
}

export default App;
